// Get ring start number, from current ring width
// int K(int w) { return w > 1 ? 4+(w-2)*4 : 1; }

// Get width of ring index
int W(int i) { return i*2+1; }

// Ulam indexing is from https://www.shadertoy.com/view/tlcyRj
int ulamId(ivec2 block2d) {
    int x = int(block2d.x);
    int y = int(block2d.y);
    int i = max(abs(x), abs(y)); // Ring index
    int w0 = W(i-1); // Ring below width
    int w1 = W(i); // Ring current width
    int h = w1/2;
    int A = w0*w0;
    int v = sign(y) == -1 && y == -h && x > -h ? 3 : (sign(x) == 1 && x == h ? 0 : (sign(y) == 1 && y == h ? 1 : 2));  
    int j;
    switch (v) {
        case 0: j =  y + (h+2); break;
        case 1: j = -x + (h+1); break;
        case 2: j = -y + (h  ); break;
        case 3: j =  x + (h-1); break;
    }
    // Original is off by 2!
    int n = A + v*w1 + j * int(i != 0) - 2;
    return n;
}

uvec2[2] CMUL(uvec2[2] a, uvec2[2] b) {
    // (ax + ayi)(bx + byi) = (ax bx âˆ’ ay by) + (ax by + ay bx)i
    //uvec2 real = u2_add(u2_mul(a.xy, b.xy), u2_neg(u2_mul(a.zw, b.zw)));
    //uvec2 imag = u2_add(u2_mul(a.xy, b.zw), u2_mul(a.zw, b.xy));

    uvec2[4] mulout;

    int nr = min(0,iFrame);

    for (int i = nr; i < 2; i++) {
    for (int j = nr; j < 2; j++) {
        mulout[i*2+j] = u2_mul(a[i], b[j]);
    }
    }

    mulout[3] = u2_neg(mulout[3]);

    uvec2[2] ri;
    for (int i = nr; i < 2; i++) ri[i] = u2_add(mulout[i], mulout[3-i]);

    return ri;
}



vec4 render( vec2 fragCoord )
{
    vec4 color = vec4(0);
    int nr = min(0, iFrame);

    vec2 uv = fragCoord.xy / iResolution.xy;
    uvec4 CfragCoord = uvec4(float_to_u2(fragCoord.x), float_to_u2(fragCoord.y));
    uvec4 CrcpiResolution = uvec4(float_to_u2(1./iResolution.x), float_to_u2(1./iResolution.y));    
    uvec4 Cuv;
    Cuv.xy = u2_mul( CfragCoord.xy, CrcpiResolution.xy);

    float aspect = iResolution.y / iResolution.x;

    vec4 ui[5];
    for (int pixel = 0; pixel < 5; pixel++) {
        ui[pixel] = texelFetch(iChannel0, ivec2(pixel, 0), 0);
    }

    vec4 centerx_v4 = ui[0];
    vec4 centery_v4 = ui[1];
    vec4 vsize_v4   = ui[2];
    vec4 opts       = ui[3];
    vec4 res        = ui[4];

    int iters   = abs(int(opts.x));
    int samples = max(1, int(opts.y));
    int hud     = (opts.x > 0.0) ? 1 : -1; // int(sign(opts.x)); // int(sign(x)) is broken??

    vec2 blockId   = res.zw;

    // let's render in blocks starting from the center and spiraling out
    // This is useful for high samples per pixel, high iterations, and full-screen
    #ifdef INCREMENTAL
    //float blocksize = 16.; // TODO: adaptive block size based on worst-case render time?
    ivec2 bid2 = ivec2(floor(((fragCoord - (iResolution.xy/2.) + (float(BLOCKSIZE)/2.)) / float(BLOCKSIZE))));
    int uid = ulamId(bid2);
    if (uid < int(blockId.x) || uid >= int(blockId.y)) {
        color = texelFetch(iChannel1, ivec2(fragCoord), 0);

        // show the next block in the queue (indicate activity & progress, helpful when changing samples and in some regions)
        #ifdef SHOW_NEXT_BLOCK
        if (uid == int(blockId.y)) { color = mix(color, vec4(vec3(.5),1), 0.5); return color; }
        #endif // SHOW_NEXT_BLOCK
        return color;
    }
    #endif // INCREMENTAL

    uvec2 center_x = v4_to_u2(centerx_v4);
    uvec2 center_y = v4_to_u2(centery_v4);
    uvec2 vsize    = v4_to_u2(vsize_v4);

    bool isRKeyPressed = texelFetch(iChannel2, ivec2(R_KEY, 1), 0).x > 0.;
    if (iFrame == 0 || isRKeyPressed) 
    {
        center_x = float_to_u2(-0.5);
        center_y = float_to_u2(0.);
        vsize = float_to_u2(1.);
    }

    uvec2[2] center = uvec2[2]( center_x, center_y );

    uvec2[2] size = uvec2[2](vsize, vsize);
    size[0] = u2_mul( size[0], float_to_u2(1./aspect) );

    uvec2[2] c;
    vec2 wuv = 2. * uv - 1.;
    for (int i=nr;i<2;i++) {
        uvec2 suv = u2_mul( size[i], float_to_u2(wuv[i]) );
        c[i] = u2_add( center[i], suv );
    }

    vec4 textColor = vec4(0);

    vec2 pxSz = 1.f / iResolution.xy;
    vec2 sampSz = vec2(1.f / float(samples));
    uvec2 sz[2] = uvec2[2]( size[0], size[1] );

    //vec4 color = vec4(0);
    for (int AAX = nr; AAX < samples; AAX++) {
    for (int AAY = nr; AAY < samples; AAY++) {

    vec2 puv = (fragCoord.xy + vec2(AAX, AAY)*sampSz) * pxSz;
    vec2 wuv = 2. * puv - 1.;
    for(int i=nr;i<2;i++) {
        uvec2 suv = u2_mul( sz[i], float_to_u2(wuv[i]) );
        c[i] = u2_add( center[i], suv );
    }

    uvec2[2] z = c;

    int i;
    float l;

    #ifdef SHOW_COBWEBS   
    vec2 maxRatio = vec2(0.);
    #endif

    for (i = nr; i < iters; i++) {
        uvec2[2] ztmp = CMUL(z, z);
        for(int j=nr;j<2;j++) z[j] = u2_add(ztmp[j], c[j]);
        for(int j=nr;j<2;j++) ztmp[j] = u2_mul(z[j], z[j]);
        uvec2 z_l_sqr = u2_add(ztmp[0], ztmp[1]);
        if (u2_to_float(z_l_sqr) > 4.) break;

        #ifdef SHOW_COBWEBS
        float ratio = abs(u2_to_float(z[1]) / u2_to_float(z[0]));
        maxRatio = max(maxRatio, vec2( 1./ratio, ratio ));
        #endif
    }       

    // Nice colormap from: https://www.shadertoy.com/view/XdsXWN
    const vec3 colourPhase = vec3(5,7,11)/80.0;
    vec3 colourPhaseStart = vec3(1);

    float mu = float(i);

    #ifdef SHOW_COBWEBS   
    if (i == iters) {
        i = 0; 
        mu = 2. * log2(max(1e-39, max(maxRatio.x, maxRatio.y)));
    }
    #endif

    if (i < iters) color.rgb += pow(sin(colourPhase.xyz * mu + colourPhaseStart)*.5+.5,vec3(1.5));

    }
    }

    color /= float(samples * samples);

    color = vec4( pow(color.rgb, vec3(1./2.2)), 1. );
    if (textColor.g > 0.) color.rgb = textColor.rgb;
    //color.rgb = mix(color.rgb, textColor.rgb, pow(textColor.a, 1.));
    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = render(fragCoord);
}

vec4 renderHUD( vec2 fragCoord )
{
    vec4 color = vec4(0);
    int nr = min(0, iFrame);

    vec2 uv = fragCoord.xy / iResolution.xy;
    uvec4 CfragCoord = uvec4(float_to_u2(fragCoord.x), float_to_u2(fragCoord.y));
    uvec4 CrcpiResolution = uvec4(float_to_u2(1./iResolution.x), float_to_u2(1./iResolution.y));    
    uvec4 Cuv;
