    float aspect = iResolution.y / iResolution.x;

    vec4 ui[5];
    for (int pixel = 0; pixel < 5; pixel++) {
        ui[pixel] = texelFetch(iChannel0, ivec2(pixel, 0), 0);
    }

    vec4 centerx_v4 = ui[0];
    vec4 centery_v4 = ui[1];
    vec4 vsize_v4   = ui[2];
    vec4 opts       = ui[3];
    vec4 res        = ui[4];

    int iters   = abs(int(opts.x));
    int samples = max(1, int(opts.y));
    int hud     = (opts.x > 0.0) ? 1 : -1; // int(sign(opts.x)); // int(sign(x)) is broken??

    vec2 blockId   = res.zw;

    uvec2 center_x = v4_to_u2(centerx_v4);
    uvec2 center_y = v4_to_u2(centery_v4);
    uvec2 vsize    = v4_to_u2(vsize_v4);

    bool isRKeyPressed = texelFetch(iChannel1, ivec2(R_KEY, 1), 0).x > 0.;
    if (iFrame == 0 || isRKeyPressed) 
    {
        center_x = float_to_u2(-0.5);
        center_y = float_to_u2(0.);
        vsize = float_to_u2(1.);
    }

    uvec2[2] center = uvec2[2]( center_x, center_y );

    uvec2[2] size = uvec2[2](vsize, vsize);
    size[0] = u2_mul( size[0], float_to_u2(1./aspect) );

    uvec2[2] c;
    vec2 wuv = 2. * uv - 1.;
    for (int i=nr;i<2;i++) {
        uvec2 suv = u2_mul( size[i], float_to_u2(wuv[i]) );
        c[i] = u2_add( center[i], suv );
    }

    vec4 textColor = vec4(0);
    if (hud > 0) {
#if 1
    // debug display of numbers
    vec2 textSz = vec2(12,20);
    vec2 textUV = fragCoord.xy / textSz;
    vec2 charUV = fract(textUV) * vec2(0.5, 1) + vec2(0.25, 0);
    vec2 charID = floor(textUV); // (col, row)

    int row = int(charID.y);
    int col = int(charID.x);
    // Show the stored state in our "ui" buffer: center x/y, size, and options
    // 5 rows, 4 numbers, 8 characters each (hex). The 9s are to put a space betwee each number
    if (row < 5 && col < 9*4) {
        int whichNum = col / 9;
        uint num = floatBitsToUint(ui[4-row][whichNum]);
        int whichDigit = 8 - (col % 9);
        int c = int((num >> (4*whichDigit)) & 0xfu);
        c += (c > 9) ? (65 - 10) : 48;
        vec2 texUV = charUV / 16.;
        if (whichDigit < 8) textColor += texture(iChannel2, texUV + fract( vec2(c, 15-c/16) / 16. ) ).xxxx;
    }
#endif
    } // hud

    //color = vec4( pow(color, vec4(1./2.2)) );
    if (textColor.g > 0.) color = textColor;

    if (hud == 1 && fragCoord.x < 450. && fragCoord.y < 110.) color.a = max(0.75, color.a);

    return color;
}

