void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*3.0-1.5,m=iMouse.xy/iResolution.xx;
    float l = sin(uv.x*uv.y+iTime);
    uv = uv+atan(l+cos(uv.x)+sin(uv.y));
    uv = uv*l*cos(l)*sin(l);
    uv = vec2(uv.x*uv.x-uv.y*uv.y,uv.y*uv.x*2.0);

    vec4 col = texture(iChannel0,uv.xy/uv.xx*uv.yy);
    col = 1.0/col-1.0;
    col = 1.0/col;

    col = sin(col.yzxw)+1.0;
    col = col.zyxw+(uv.x+uv.y);
    uv = uv+col.rg;
    uv = uv*col.rb;
    col = col*sin(uv.x+iTime)*cos(uv.y+iTime);
    col = round(col*(1.0/abs(sin(iTime))))/(1.0/abs(sin(iTime)));
    col = vec4(sin(col.x),cos(col.y),tan(col.x*col.y),col.w);

    // Output to screen
    fragColor = vec4(col);
}
