void mainImage( out vec4 O, vec2 I)
{
    //Resolution for scaling
    vec3 r = iResolution,
    //Ray direction for raymarching and sky color
    d = (vec3(I+I,r)-r)/r.x,
    //Ray origin position
    p = r-r,
    //Iteration counter
    i = p;

    //Loop 200 times
    for(; i.x<2e2;
        //Raymarch with heightmap
        p += d*(p.y+.3-.3*texture(iChannel0,(p*r+iTime*r*.3).xz/4e2).r)/r)
        //Swap octave scale every 5 iterations
        r = exp(mod(++i,5.));

    //Set color using d, sun, depth and heightfog
    O.grb = .5*d + .03*++d/length(d.xy-1.3) - .7/++p.z - min(.2+p+p,0.).y;
}

