void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;
    float r = .4;
    vec2 shift = vec2(0);
    float ii = (sin(iTime*.5-pi*.5)*.5+.5)*50.;//mod(iTime*5., 100.);
    for(float i=0.;i<ii;i++){
        vec2 h = N(i*sin(iTime*.1)*.05)*vec2(.05, .001);//hash(vec2(i));
        vec2 d = vec2(0, .0005);//vec2(0, sin(iTime*5.*h.y)*.05);
        float a = iTime*i*.5;
        mat2 rot = Rot(a);
        mat2 rot1 = Rot(-a);
        d*=rot1;
        vec2 uv1 = uv*rot;
        uv1 += 2.*i*r/ii-r;//hash1(vec2(i))*.1;

        shift += uv1.x<.0 ? -d : d;
    }
    uv += shift;
    vec3 col = vec3(0);
    float d = circle(uv, r);
    col+=vec3(.2+(abs(shift))*100.,1)*smoothstep(.01,.0, d);
    col = pow(abs(col)+.0, vec3(.5));
    fragColor = vec4(col,1.0);
}

#define pi 3.1415926535

float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5*(a-b)/k, 0.0, 1.0);
    return mix(a, b, h) - k*h*(1.0-h);
}

float circle(vec2 p, float r){
   return length(p)-r;
}

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

vec2 N(float angle) {
// angle to vector
    return vec2(sin(angle), cos(angle));
}

