void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 main_col=texture(iChannel0,uv).rgb;
    vec3 sec_col=texture(iChannel1,uv).rgb;

    uv.x=cos(uv.x)*main_col.r*sec_col.b;
    uv.y=sin(uv.y)*main_col.b*sec_col.r;


    vec3 col = distance(uv.x*uv.x+uv.y*uv.y,1.)*vec3(1,1,1)*tan(main_col.g);

    col = 0.5 + 0.5*cos(iTime+uv.xyx+col)*col;

    fragColor = vec4(col,1.0);
}
