int seed = 2;
int frand(void) { seed = seed*0x343fd+0x269ec3; return (seed>>16)&32767; }
float rand(void) { return float(frand())/32767.0/1000.0; }
float offset =0.00005;

float eval(vec2 uv){
    vec2 ab = uv;
    vec2 aibi;
    for(int i = 0; i<100; i++){
        aibi.x = ab.x*ab.x-ab.y*ab.y;
        aibi.y = ab.x*ab.y*2.0;
        ab = aibi+uv;
    }
    if (length(ab)<100.0){
        return 1.0;
    }
    else{
        return 0.0;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ((fragCoord-.5*iResolution.xy)/iResolution.y*1.8);
    uv.x -= iTime*1.5;
    uv /= iTime;
    float mean = 0.0;
    for (int i = 0; i<50; i++){
        mean += eval(uv+(vec2(rand()-0.5,rand())))/50.0;
    }
    fragColor = vec4(mean,mean,mean,1);
}
