float box( vec2 P, vec2 S){
    P = smoothstep(S, S-.2, abs(2.*P-1.) );
    return P.x*P.y;
}
#define random(P,k,t) fract(sin(dot( P+sin(t), vec2(.9,.98))) * (43757.617 + k+P.x) )
#define add(a,b)      clamp(a-(b)*a, 0.,1.) + b
#define circle(P,r)   smoothstep( r*.02, r*.01, dot(P-.5,P-.5)*4. -r )
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 mo = iMouse.xy/iResolution.xy;

    uv =(uv-.5)*100. ;
    mo = (mo - 0.500)/40.;

    uv.y *= iResolution.y/iResolution.x;
    uv += mo/0.001;
    uv = (uv /(1.-uv.x*mo.x)) ;
    uv = (uv /(1.-uv.y*mo.y)) ;
    vec3 color = vec3(0.);

    float screen = box(uv,vec2(18.408-(uv.x*uv.x)*(uv.y*uv.y)*0.00005)) *random(floor(uv*0.904),0.,iTime) ;
    float border = (box(uv,vec2(28.456-(uv.x*uv.x)*(uv.y*uv.y)*0.00004)) -box(uv,(20.-vec2(uv.x*uv.x)*(uv.y*uv.y)*0.00005)))*0.092 ;
    float button = (0.1,0.9,circle(uv-(-0.280,-0.410)*30.,2.624)*-0.160)+(circle(uv-((-2.771)/10.,(-4.068)/10.)*30.,3.040)*-0.136);
    float light = (3000.-(uv.y*uv.y+uv.x*uv.x))/7000. ;
    screen= screen;
    light = smoothstep(0.2,0.5,max(light,0.1));
    float tv = screen+border;
    tv = button+tv;
    color = vec3(add(light/3.200,tv));

    // Output to screen
    fragColor = vec4(color,1.0);
}
