float shadow(float time,vec2 uv)
{


    float velx=3.0;
    float freqx=2.0;
    float vely=3.0;
    float freqy=2.0;
    float timeshiftx=(iTime*velx)+(uv.x*freqx);
    float timeshifty=(iTime*vely)+(uv.y*freqy);
    float shadow=0.5+cos(timeshiftx)*sin(timeshifty)*0.5;
    return shadow;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float wiggle = sin(iTime*2.0+(uv.x*5.0))*0.01;
    // Time varying pixel color
    fragColor = vec4(0,0,1,1)*shadow(iTime,uv);

    if(uv.y>0.33+wiggle){
        fragColor = vec4(1,1,1,1)*shadow(iTime,uv);
    }

    if(uv.y>0.66+wiggle){
        fragColor = vec4(1,0,0,1)*shadow(iTime,uv);
    }




    // Output to screen
    //fragColor = vec4(1.0,1.0,1.0,1.0)*shadow;

}
