#define PI 3.14159265359

// Rectangle drawing method
// Author: Lydia 
float rect(in vec2 uv, in vec2 pos, in vec2 size) {
    // bottom-left
    vec2 bl = step(pos, uv) * step(pos, uv);
    float rect = bl.x * bl.y;

    // top-right
    vec2 tr = step(uv, pos + size) * step(uv, pos + size);
    rect *= tr.x * tr.y;

    return rect;
}

// Make Square
// Author: Lydia 
// Adjust coordinates to compensate for 16:9
vec2 mksq(in vec2 co) {
    return vec2(co.x * (iResolution.y / iResolution.x), co.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Draw squares
    float sp = float(0.9);
    vec2 amp = mksq(vec2(0.3));
    vec2 size = mksq(vec2(0.3));
    vec2 cen = vec2(0.5) - (size / 2.0);

    vec2 pos = vec2(cen.x + amp.x * sin(iTime * sp), cen.y + amp.y * cos(iTime * sp)); 
    float mvsqs = rect(uv, pos, size);

    pos = vec2(cen.x + amp.x * sin((iTime * sp) + PI), cen.y + amp.y * cos((iTime * sp) + PI)); 
    mvsqs += rect(uv, pos, size);

    pos = vec2(cen.x + amp.x * sin((-iTime * sp) + (PI / 2.0)), cen.y + amp.y * cos((-iTime * sp) + (PI / 2.0))); 
    mvsqs += rect(uv, pos, size);

    pos = vec2(cen.x + amp.x * sin((-iTime * sp) - (PI / 2.0)), cen.y + amp.y * cos((-iTime * sp ) - (PI / 2.0))); 
    mvsqs += rect(uv, pos, size);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0.0, 2.0, 4.0));
    col = mix(vec3(0.0), col, mvsqs); 

    // Output to screen
    fragColor = vec4(col,1.0);
}
