void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.x;


    vec3 cam=vec3(0.,1.,0.);
    vec3 ball=vec3(sin(iTime),0.5,4.);
    float rad=.5;
    float travel=0.;    
    int steps=100;
    float world=100.;
    vec3 p=cam;
    vec3 dir=normalize(vec3(uv.x,uv.y,sin(iTime)*.5+1.));

vec3 col =vec3(0.);
    for (int i=0;i< steps;i++){
        float idist=distance(p,ball)-rad;
        travel+=idist; //update distace traveled. 
        if (travel>world){
        col=vec3(float(i)/100.);break;//set for clarity
        }
        if (idist<0.01){
        col=normalize(abs(p-ball))*.6;//vec3(0.);break;
        }
        p=p+dir*idist;       
}
    // Output to screen
    fragColor = vec4(col,1.0);
}
