void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float SPEED = 50.0;
    float ZOOM = 0.6;
    vec4 COLOR_DARK = vec4(46./255., 37./255., 30./255., 1.0);
    vec4 COLOR_LIGHT = vec4(237./255., 218./255., 202./255., 1.0);


    float offset = iTime * SPEED / 100.0;

    vec2 position = vec2(int(fragCoord.x/ZOOM/100.0 + offset), int((iResolution.y - fragCoord.y)/ZOOM/100.0 + offset));


    if (int(position.x) % 2 == int(position.y) % 2) {
        fragColor = COLOR_DARK;
    } else {
        fragColor = COLOR_LIGHT;
    }
}
