#define FREQUENCY 100.0
#define SPEED 4.0
#define PI 3.141592653589793

float drawCircle(vec2 uv) {
	float a = atan(uv.y, uv.x);
	a = a - PI;

    vec2 newuv = vec2(uv.x * cos(a), uv.y * sin(a));
	float d = length(newuv);
	float c = cos(d * FREQUENCY - iTime * SPEED);

	return c;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float ratio = iResolution.x / iResolution.y;
	vec2 uv = fragCoord/iResolution.y;

    // centering
    float wiggle = 0.1;
    vec2 uv2 = uv - vec2( cos(iTime)*wiggle + ratio*0.5, sin(iTime)*wiggle+0.5);

    // shape
    float c = drawCircle(uv2);

    // antialias
	c = c / fwidth(c);

    fragColor = vec4(vec3(c,c,.5), 1.0);
}
