void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec4 col = texture(iChannel0, uv);

    float modX = floor(mod(fragCoord.x,2.0));    
    float modY = floor(mod(fragCoord.y,2.0));

    vec3 bat = vec3(col.rgb);
    if(modX == 0.0 && modY == 0.0){
        bat = step(0.25, bat);
    }
    else if (modX == 1.0 && modY==0.0) {
        bat = step(0.5, bat);
    }
    else if(modX == 0.0 && modY == 1.0) {
        bat = step(0.75, bat);
    }
    else if(modX == 1.0 && modY == 1.0) {
        bat = step(0.1, bat);
    }

    // Output to screen
    fragColor = vec4(bat,1.0);
}
