// three oscillating signals
// see cube A

void mainImage( out vec4 O, vec2 u )
{
    vec2 uv = u.xy / R.xy * 2.0 - 1.0;
	uv.y *= iResolution.y / iResolution.x;

    vec3 rayDir = normalize(vec3(uv, 1.0));


    vec4 T = texture(iChannel0, rayDir);
    O = mix(O, T, T.a);
}

void mainCubemap( out vec4 fragColor, in vec2 fragCoord, in vec3 rayOri, in vec3 rayDir )
{
    // three oscillating signals

    //                     +----- 1D oscillating signal
    //                     |            +-------- 2D oscillating signal
    //                     |            |           +------- 3D oscillating signal
    //                     |            |           |
    vec3 signal = vec3(cos(iTime), -sin(iTime), sin(iTime));

    // consider signal to be a direction ....

    // pack direction as color
    vec3 col = 0.5 + 0.5*normalize(signal);

    // Output to cubemap
    fragColor = vec4(col,1.0);
}
