#define MODE 4 //0 to 5

#define ITERATIONS 10000



float arctan2(float y, float x)
{
#if MODE == 0
    return atan(y, x);
#elif MODE == 1
    return newfastatan2(y, x);
#elif MODE == 2
    return newfastatan2_A(y, x);
#elif MODE == 3
    return newfastatan2_B(y, x);
#elif MODE == 4
    return canonicfastatan2_A(y, x);
#elif MODE == 5
    return canonicfastatan2_B(y, x);    
#elif MODE == 6
    return newestfastatan2(x, y);    
#elif MODE == 7
    return fastatan2withacosapprox(y, x);  
#else 
    return atan(y, x);
#endif
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 4.0 - 2.0;
    vec2 origuv = uv;

    //origuv = normalize(origuv);

    float v = 0.0;

    for(int j = 0; j < ITERATIONS; j++)
    {
        v = arctan2(uv.x, uv.y);
        uv.xy -= v;    
    }

    float mousefact = iMouse.x/iResolution.x;       
    float err = abs(arctan2(origuv.y, origuv.x) - atan(origuv.y,origuv.x));        
    if(err > mousefact*0.03) v = 1.0;        


    //if(origuv.x < 5.0) v = arctan2(origuv.y, origuv.x)/(PI*2.)+0.5;   

    //v = abs(atan(uv.x, uv.y) - arctan2(uv.x, uv.y))/0.01082;

    //v = fract(v);



    fragColor = vec4(v);
}

