#define PI 3.14159256

//彩虹插色函数
vec3 rainbowColor(float t) {
    return .5 + .5 * cos(6.283 * (t + vec3(0., .33, .67)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 坐标转换至屏幕中心变为-1至1
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;
    //0 - 0.5
    float len = length(uv);
    //相对中心的角度
    float angle = atan(uv.y, uv.x);
    //n 2PI
    float f = angle / PI / 2. - iTime;
    f = fract(f);
    // 颜色
    vec3 circleColor = rainbowColor(f);

    //画圆，并且彩虹色根据f再和黑色混色
    circleColor = len < 0.4 || len > 0.5 ? vec3(0.): mix(circleColor, vec3(0.), 1. - f);

    // Output to screen
    fragColor = vec4(circleColor,1.0);
}
