void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float speed = 0.3;
    // sky
    vec3 upSkyColor = vec3(0.25,0.5,1.0);
    // floor 
    vec3 downSkyColor = vec3(1.0);
    // sky Color
    vec3 col = mix(downSkyColor,upSkyColor,uv.y);
    vec2 pos = iResolution.xy*0.5 + vec2(0.0,100.0); 
    vec2 sun = pos;//sun position
    vec3 sunColor = vec3(1.5,1.0,0.0);
    float radius = 50.0;
    float dis = distance(sun,fragCoord);

    float stepOffset = 90.0;
    if ( dis < radius) {
        col = sunColor;
    } else if (dis > radius && dis < radius+stepOffset) {
        float s = smoothstep(radius,radius+stepOffset,dis);
        col = mix(sunColor,col,s);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
