#define TAU 6.28318530718

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // Declare the two colours
    vec3 cola = vec3(0.0);
    vec3 colb = vec3(0.1);
    // Declare the screen size (for pixelation)
    vec2 screen = vec2(240, 135);
    // Pixelate the UV
    uv = floor(uv * screen)/screen;
    // Center the UV on the screen
    uv -= vec2(0.5);

    // Calculate the polar coordinates
    float r = distance(uv, vec2(0.0));
    float a = atan(uv.y, uv.x);

    // Set the colour to cola...
    vec3 col = cola;

    // ...the if its angle is inside a stripe,
    // its colour is set to colb.
    if ( fract((a + iTime)/TAU*8.0) < 0.5 ) {
        col = colb;
    }

    fragColor = vec4(col,1.0);
}
