vec2 Rotate2D(vec2 uv,const float r) {
  return vec2(uv.x*cos(r)-uv.y*sin(r),uv.y*cos(r)+uv.x*sin(r));
}

float Noise3D(float x,float y,float z) {
  return fract(sin(x*112.9898+y*179.233+z*237.212)*43758.5453);
}

void mainImage(out vec4 fragColor,in vec2 fragCoord) {  
    vec2 uv = fragCoord.xy/iResolution.xy;
    float time=iTime*64.;
    float zoom=0.5+iTime/50.;

    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;
    uv*=pow(zoom,3.);
    uv*=0.9;
    uv-=0.5;
    vec2 uvid=uv;
    uv-=floor(uv);
    uv-=0.5;
    uvid=floor(uvid);

    uv=Rotate2D(uv,(sin(time/120.+Noise3D(uvid.x,uvid.y,0.)*2.)*2.)*(float(Noise3D(uvid.x,uvid.y,1.)>0.5)*2.-1.));
    uv=abs(uv);

    float u_col_one=0.;
    u_col_one=max(float(max(uv.x*2.,uv.y/2.)<0.2),float(max(uv.x/2.,uv.y*2.)<0.2));
    u_col_one-=float(length(uv)<0.25);
    u_col_one=abs(u_col_one);
    u_col_one+=float(length(uv)<0.1);
    u_col_one*=Noise3D(uvid.x,uvid.y,1.);
    vec3 u_col = vec3(u_col_one*Noise3D(uvid.x,uvid.y,3.1),u_col_one*Noise3D(uvid.x,uvid.y,4.2),u_col_one*Noise3D(uvid.x,uvid.y,5.3));
    fragColor = vec4(u_col,1.0);
}
