float Cardioid(vec2 uv, float size, vec2 offset) {

  float x = uv.x - offset.x;
  float y = uv.y - offset.y;
  float angle = (atan(y,x) + 3.14156); //0-2PI
  float l = length(vec2(x, y)); 
  float r = (size - sin(angle)) > l *3. ? 1. :0. ; 
  return r ; 

  }

vec3 drawScene(vec2 uv) {
  vec3 col = vec3(1);
  float heart = Cardioid(uv, 0.6, vec2(0., -0.2));

  col = vec3(heart); 
  col = mix(vec3(1, 0, 0), col, step(heart,0. ));

  return col ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord/iResolution.xy; // <0, 1>
  uv -= 0.5; // <-0.5,0.5>
  uv.x *= iResolution.x/iResolution.y; // fix aspect ratio

  vec3 col = drawScene(uv);

  // Output to screen
  fragColor = vec4(col,1.0);
}
