void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 color = vec3(0.25, 0.6, 0.99);
    float thickness = iMouse.x/iResolution.x;
    thickness = 0.4;
    float fade = 1.5/iResolution.y; //0.005;

    vec2 uv = (fragCoord -.5 * iResolution.xy) / iResolution.y;

    float dis = 0.5 - length(uv);
    color *= smoothstep(0.0, fade, dis);
    color *= smoothstep((thickness/2.0) + fade, thickness/2.0, dis);
    fragColor.rgb = color;
}
