#define S(a,b,t) smoothstep(a,b,t)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //uv.x *= iResolution.x/iResolution.y;
    vec3 col = vec3(texture(iChannel0, uv));
    fragColor = vec4(col,1.0);
}

vec3 kernel(ivec2 fragCoord){
    vec3 num = vec3(0.);
    vec3 preNum = texelFetch(iChannel1, fragCoord, 0).rgb;
    int range = 1;
    for(int i = -range; i < range+1 ; i++ ){
        for(int j = -range; j < range+1 ; j++ ){
            if(i != 0 || j != 0){
                num += texelFetch(iChannel1, fragCoord + ivec2(i,j), 0).rgb;
            }  
        }
    }

    vec3 diff = num/8.0 - preNum;
    if(diff.r > 0.){
        preNum = preNum - diff*.001;
    }else{
        preNum = preNum + 0.01;
    }

    return preNum;
}

vec3 getColor(ivec2 fragCoord){
    vec3 num = kernel(fragCoord);
    return num;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(texture(iChannel0, uv));

    /**/
    if(iFrame < 10){
        col = vec3(texture(iChannel0, uv));
    }else{
        col = getColor(ivec2(fragCoord));
    }
    /**/
    fragColor = vec4(col.r, col.r, col.r,1);
}
