const float PI = 3.14;
const float A = 0.05;
const float F = 12.;
const float P = 2.;
const float FA = 1.;

void mainImage( out vec4 O, in vec2 u )
{
    vec2 Res = iResolution.xy, 
        U = (2. * u - Res) / Res.xy;
    float at = sqrt(1. - U.x * U.x);
    float y = A * sin(F * U.x + P * sin(iTime)) * cos(FA * iTime);
    O = vec4(0., 0., exp(-(U.y - y * at)/0.01), 1.);
}
