float spinSpeed = 0.1;

//based on water sample
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv *= -sin(uv.x) - cos(uv.x);
    uv *= .1;

    vec3 col = texture(iChannel0,uv + iTime * spinSpeed).rgb - vec3(1.,1.,1.);
    col *= -1.;

    vec2 addUV = uv + vec2(iTime * spinSpeed,0.0);
    float add = texture(iChannel1,addUV).r;
    add = smoothstep(1.,0.01,add);
    col+=add;

    // Output to screen
    fragColor = vec4(col,1.0);
}
