void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.y;
    uv*=12.0;
    float t = step(1.0,mod(floor(uv.y),2.0))>0.?1.0:-1.0;
    float t1 = step(1.0,mod(floor(uv.x),2.0))>0.?1.0:-1.0;

    float b = cos(iTime)>0.?1.0:-1.0;
    float offx = b>0.?b*t*sin(iTime)*2.0:0.;
    float offy = b>0.?0.:b*t1*sin(iTime)*2.0;
    uv += vec2(offx,offy);
    uv = fract(uv);


    float a = 1.0-step(0.3,distance(uv,vec2(0.5)));
    vec3 col = vec3(a);
    // Output to screen
    fragColor = vec4(col,1.0);
}
