float p1 = 0.1f; // 0 - 1

vec2 offset(float progress, float x, float theta)
{
    float phase = progress * progress + progress + theta;
    float shifty = p1 * progress * cos(10.0 * (progress + x));
    return vec2(0, shifty);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = mod(iTime,4.)*0.25;
    vec2 uv = fragCoord/iResolution.xy;
    vec4 c1 = texture(iChannel0,uv + offset(time, uv.x, 0.0));
    vec4 c2 = texture(iChannel1,uv + offset(1.0 - time, uv.x, 3.14));
    fragColor = mix(c1, c2, time);

}
