float random(vec3 a){
    float seed = 5.20948;
    for(float i=0.;i<10.;i++){
        seed = fract(sin(a.x*646.7974*seed)*3498. + 
                     sin(a.y*593.2093*seed)*9283. + 
                     sin(a.z*842.5872*seed)*5398.);
    }return seed;
}

float layer(vec3 uv){
    vec3 id = floor(uv*10.);
    vec3 luv = smoothstep(0., 1., fract(uv*10.));
    float t = 0.;
    float b = 0.;
    float u = 0.;
    float d = 0.;
    for(float i=0.; i<2.; i++){
        float bl = random(id+vec3(0., 0., i));
        float br = random(id+vec3(1., 0., i));
        if(i == 0.)b = mix(bl, br, luv.x);
        else u = mix(bl, br, luv.x);
        float tl = random(id+vec3(0., 1., i));
        float tr = random(id+vec3(1., 1., i));
        if(i == 0.)t = mix(tl, tr, luv.x);
        else d = mix (tl, tr, luv.x);}
    float a = mix(b, t, luv.y);
    float z = mix(u, d, luv.y);
    return mix(a, z, luv.z);
}

float noise(vec3 uv, int layers){
    float col = 0.;
    for(float i=1.; i<float(layers)+1.; i++){
        float t = pow(2., i-1.);
        col += layer(uv*t)/t;
    }return col / (2.-1./pow(2., float(layers)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 R = iResolution.xy;
    vec3 uv = vec3(fragCoord/R.x, iTime/10.);
    float col = 0.;

    int layers = 5;
    col += noise(uv, layers);

    fragColor = vec4(vec3(col),1.0);
}
