// https://www.shadertoy.com/view/NstSR8 Cast Voxels March Sub-Objects
// fork of https://www.shadertoy.com/view/4dX3zl Branchless Voxel Raycasting by fb39ca4
// (with loop optimization by kzy), DDA based on http://lodev.org/cgtutor/raycasting.html
// raymarching subobjects addon by jt

// tags: 3d, raymarching, raycasting, voxel, dda, textured, subobjects, fork

#define pi 3.1415926

mat3 yaw_pitch_roll(float yaw, float pitch, float roll)
{
    mat3 R = mat3(vec3(cos(yaw), sin(yaw), 0.0), vec3(-sin(yaw), cos(yaw), 0.0), vec3(0.0, 0.0, 1.0));
    mat3 S = mat3(vec3(1.0, 0.0, 0.0), vec3(0.0, cos(pitch), sin(pitch)), vec3(0.0, -sin(pitch), cos(pitch)));
    mat3 T = mat3(vec3(cos(roll), 0.0, sin(roll)), vec3(0.0, 1.0, 0.0), vec3(-sin(roll), 0.0, cos(roll)));

    return R * S * T;
}

#define MAX_DIST 1000.0

// https://iquilezles.org/articles/intersectors
vec2 boxIntersection( in vec3 ro, in vec3 rd, vec3 boxSize )
{
    vec3 m = 1.0/rd;
    vec3 n = m*ro;
    vec3 k = abs(m)*boxSize;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
    float tN = max( max( t1.x, t1.y ), t1.z );
    float tF = min( min( t2.x, t2.y ), t2.z );
    if( tN>tF || tF<0.0) return vec2(-1.0);
    return vec2( tN, tF );
}

// https://iquilezles.org/articles/distfunctions
float sdSphere(vec3 p, float d)
{
    return length(p) - d;
}

float sdBox( vec3 p, vec3 b )
{
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

#define halfwallsize 0.05

float sdMixed(vec3 p, bvec3 walls)
{
    float d = MAX_DIST;
    if(walls.x) d = min(d, sdBox(p + vec3(0.5 - halfwallsize, 0.0, 0.0), vec3(halfwallsize, 0.49, 0.49)));
    if(walls.y) d = min(d, sdBox(p + vec3(0.0, 0.5 - halfwallsize, 0.0), vec3(0.49, halfwallsize, 0.49)));
    if(walls.z) d = min(d, sdBox(p + vec3(0.0, 0.0, 0.5 - halfwallsize), vec3(0.49, 0.49, halfwallsize)));
    return d;
}

#define halfboundsize 6

bool getBoundingShapeVoxel(ivec3 c) {
    vec3 p = vec3(c) + vec3(0.5);
    float d = sdBox(p, vec3(halfboundsize));
    return d < 0.0;
}

#define PI 3.1415926

float checker(vec3 p)
{
    //return step(0.5, length(1.0 - abs(2.0 * fract(p) - 1.0))); // dots
    return step(0.0, sin(PI * p.x + PI/2.0)*sin(PI *p.y + PI/2.0)*sin(PI *p.z + PI/2.0));
    //return step(0.0, sin(p.x)*sin(p.y)*sin(p.z));
}

mat2 rotate(float t)
{
    return mat2(vec2(cos(t), sin(t)), vec2(-sin(t), cos(t)));
}

#define EPSILON 0.001

// raymarch subobject
float march(vec3 ro, vec3 rd, float tmin, float tmax, bvec3 walls)
{
    for(float t = tmin; t < tmax;)
    {
        float h = sdMixed(ro + rd * t, walls);
        if(h < EPSILON)
            return t;
        t += h;
    }
    return MAX_DIST;
}

// based on https://iquilezles.org/articles/normalsSDF tetrahedron normals
vec3 normal(vec3 p, bvec3 walls)
{
    const float h = EPSILON;
    const vec2 k = vec2(1,-1);
    return normalize(k.xyy * sdMixed(p + k.xyy * h, walls) +
                     k.yyx * sdMixed(p + k.yyx * h, walls) +
                     k.yxy * sdMixed(p + k.yxy * h, walls) +
                     k.xxx * sdMixed(p + k.xxx * h, walls));
}

vec4 process_subobject(vec3 ro, vec3 rd, float tmin, float tmax, bvec3 walls)
{
    float d = march(ro, rd, tmin, tmax, walls);
    vec3 n = normal(ro + rd * d, walls);
    return vec4(n, d);
}

// "The raycasting code is somewhat based around a 2D raycasting toutorial found here:
//  http://lodev.org/cgtutor/raycasting.html" (fb39ca4)

#define MAX_RAY_STEPS 64

void mainImage( out vec4 fragColor, in vec2 I )
{
    bool demo = true;//all(lessThan(iMouse.xy, vec2(10.0)));
    vec2 R = iResolution.xy;
    //I = 2.0 * I.xy / R - 1.0;
    //I.x *= R.x / R.y;
    I = (2.0 * I - R) / R.y; // concise pixel-position mapping thanks to Fabrice
    float yaw = 2.0 * pi * float(iMouse.x) / float(R.x);
    float pitch = -pi/4.0 + pi - pi / 2.0 * float(iMouse.y) / float(R.y);
    yaw = !demo ? yaw : 2.0 * pi * fract(iTime * 0.05);
    pitch = !demo ? pitch : /* 1.5 * */ pi/2.0;

    vec3 ray_pos = vec3(0.0, 0.0, -15.0);
    vec3 ray_dir = vec3(I.x, I.y, 2.0); // NOTE: un-normalized direction appears to work fine here!
    //ray_dir = normalize(ray_dir); // looks like ray-direction does not need to be normalized

    mat3 M = yaw_pitch_roll(yaw, pitch, 0.0);
    ray_pos = M * ray_pos;
    ray_dir = M * ray_dir;

    //ray_pos.z -= 0.0;

    ray_dir = normalize(ray_dir);

    vec3 color = vec3(0.0);
    vec4 result = vec4(vec3(0.0), MAX_DIST);

    {
        vec3 deltaDist = 1.0 / abs(ray_dir);
        ivec3 rayStep = ivec3(sign(ray_dir));
        ivec3 mapPos = ivec3(floor(ray_pos));
        vec3 sideDist = (sign(ray_dir) * (vec3(mapPos) - ray_pos) + (sign(ray_dir) * 0.5) + 0.5) * deltaDist;

        for (int i = 0; i < MAX_RAY_STEPS; i++)
        //for (int i = min(iFrame,0); i < MAX_RAY_STEPS; i++) // prevent unrolling loop to prevent crash
        {
            if(min(abs(mapPos.x), min(abs(mapPos.y),abs(mapPos.z))) > 5) // explicit sky-box (letting loop run-out without hitting a wall results in artifacts)
                break;

            if (getBoundingShapeVoxel(mapPos))
            {
                bvec3 walls = lessThan(vec3(texture(iChannel0, 0.1 * vec3(mapPos))), vec3(0.1, 0.5, 0.1));
                //bvec3 walls = lessThan(vec3(texelFetch(iChannel0, mapPos + halfboundsize, 0)), vec3(0.1, 0.5, 0.1));
                if(any(walls)) // skip empty voxels
                {
                    // Near bounding-box side can be reconstructed from dda, howvever we need far bounding-box side, too.
                    // float d = length(vec3(mask) * (sideDist - deltaDist)) / length(rayDir);
                    // It appears to be easier to just do the box-intersection here (potential for optimization).
                    vec2 bounds = boxIntersection(ray_pos - vec3(mapPos) - vec3(0.5), ray_dir, vec3(0.5) ); // bounding box

                    result = process_subobject(ray_pos - vec3(mapPos) - vec3(0.5), ray_dir, bounds.x, bounds.y, walls);
                    if(result.w > 0.0 && result.w < bounds.y)
                    {
                        //color = vec3(1.0); // plain
                        color = 0.5 + 0.5 * vec3(texture(iChannel0, 0.1 * vec3(mapPos))); // some random colors to improve visualization
                        //color = 0.5 + 0.5 * normalize(result.xyz); // normal colors
                        vec3 dst = ray_pos + ray_dir * result.w;
                        //color *= texture(iChannel0, dst).xyz;
                        color *= vec3(0.5 + 0.5 * checker(dst));

                        vec3 fogcolor = vec3(0.25, 0.4, 0.5); // fog
                        //vec3 fogcolor = vec3(0.75, 0.6, 0.3); // smog
                        color *= mix(fogcolor, color, exp(-result.w * result.w / 200.0)); // fog for depth impression & to suppress flickering

                        break;
                    }
                }
            }

            // Core of https://www.shadertoy.com/view/4dX3zl Branchless Voxel Raycasting
            bvec3 mask = lessThanEqual(sideDist.xyz, min(sideDist.yzx, sideDist.zxy));
            sideDist += vec3(mask) * deltaDist;
            mapPos += ivec3(vec3(mask)) * rayStep;
        }
    }

    vec3 ambient = vec3(0.1);
    vec3 lightdir = normalize(vec3(3.0, 2.0, 1.0));
    color *= mix(ambient, vec3(1.0), clamp(dot(lightdir, result.xyz), 0.0, 1.0));

    fragColor.rgb = sqrt(color);
}
