void mainImage( out vec4 c, vec2 p ) {

    // set position
    vec2 v = iResolution.xy;
    p = (p-v*.5)*.5 / v.y;

    // breathing effect
    p += p * sin(dot(p, p)*20.-iTime) * .04;

    // color
    c *= 0.;
    for (float i = .5 ; i < 8. ; i++)

        // fractal formula and rotation
        p = abs(2.*fract(p-.5)-1.) * mat2(cos(.01*(iTime+iMouse.x*.1)*i*i + .85*vec4(1,8,3,1))),

        // coloration
        c += exp(-abs(p.y)*5.) * (cos(vec4(2,3,1,0)*i)*.5+.5);



    // palette
    c.gb *= .5;

}
