void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float Pi = 6.28318530718; // Pi*2

    // GAUSSIAN BLUR SETTINGS
    float Directions = 16.; // BLUR DIRECTIONS (Default 16.0 - More is better but slower), 8,64,32
    float Quality = 32.; // BLUR QUALITY (Default 4.0 - More is better but slower) 4,64,32
    float Size = 16.; // BLUR SIZE (Radius) 0-128

    vec2 Radius = Size / iResolution.xy;
    vec4 Color = texture(iChannel0,uv);
    if (uv.x > 0.7f || uv.x < 0.3f)
    {
        // Zoom
        float zoom = 2.;
        float offset_x = 0.25;
        float offset_y = 0.25;
        vec2 xy = uv;
        xy = vec2(offset_x + xy.x / zoom, offset_y + xy.y / zoom);
        // Blur calculations
        for (float d = 0.0; d < Pi; d += Pi / Directions)
        {
            for (float i = 1.0 / Quality; i <= 1.0; i += 1.0 / Quality)
            {
                Color += texture(iChannel0,xy + vec2(cos(d), sin(d)) * Radius * i);
            }
        }
        Color /= Quality * Directions - 15.0;
    }
    fragColor = Color;
}
