#define pi 3.14158

mat2 rot(in float t) {
    float c = cos(t), s = sin(t);
    return mat2(c,s,-s,c);
}

float hilal(in vec2 p, in float r) {
    return max(length(p)-r,-(length(p-vec2(.215*r))-.925*r));
}

float najm(in vec2 p, in float r) {
    float mag = length(p);
    float a = atan(p.y,p.x);
    float m = (2.*pi)/5.;
    float t = mod(a,m)-.5*m;
    vec2 tf = vec2(cos(t),sin(t)) * mag;
    vec2 k = rot(-pi*.4) * vec2(tf.x,abs(tf.y));
    return k.x-r;
}

void mainImage( out vec4 O, in vec2 u )
{
    vec2 R = iResolution.xy;
    vec2 p = (2.*u-R)/R.y;

    float w = sin(.5*(1.5*p.x+p.y)*12.+iTime*6.)*.25+.75;

    p *= 1. + (.06-.03*w);

    float t1 = hilal(p-vec2(.25,0),.625);
    float t2 = najm(p-vec2(.46,.21),.055);
    float t3 = p.x+1.;
    float t = min(min(t1,t2),t3);
    float v = smoothstep(3./R.y,0.,t);

    O.rgb = mix(vec3(0.08,0.25,0.08),vec3(1),v);

    O.rgb *= w;

    //O = vec4(p,0.0,1.0);
}
