void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 frag = texture(iChannel0, uv);

    float wid1 = 0.02;
    float hei1 = 0.005;
    float wid2 = 0.003;
    float hei2 = 0.03;

    vec3 neg = vec3(1.0-frag.r, 1.0-frag.g, 1.0-frag.b);
    float mid = 0.0;

    if (uv.x > 0.5-wid1 && uv.x < 0.5+wid1 && uv.y > 0.5-hei1 && uv.y < 0.5+hei1) {
        mid += 0.5;
    }

    if (uv.x > 0.5-wid2 && uv.x < 0.5+wid2 && uv.y > 0.5-hei2 && uv.y < 0.5+hei2) {
        mid += 0.5;
    }

    if (mid >= 0.5) {
        frag.rgb = neg;
    }

    fragColor = frag;
}
