//rgbGuy

//given 3 points and a value between 0,1 this gives out the point on bezier curve
vec2 bezier(vec2 p1, vec2 p2, vec2 p3, float t)
{
    vec2 p;
    p.x = p1.x*t*t + p2.x*2.0*t*(1.0-t) + p3.x*(1.0-t)*(1.0-t);
    p.y = p1.y*t*t + p2.y*2.0*t*(1.0-t) + p3.y*(1.0-t)*(1.0-t);
    return p;
}

//checks if point is inside a circle with centre and radius
bool pointInsideCircle(vec2 point, vec2 centre, float r)
{
    if(distance(point, centre) < r) return true;
    else return false;
}

#define radius 0.005
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 color = vec4(0.2, 0.2, 0.2,1.0);

    vec2 c1 = vec2(0.25,0.5); //circle1 //redcircle
    vec2 c2 = vec2(0.75,0.5); //circle2 //bluecircle
    vec2 cm = vec2(0.2);

    if(bool(iMouse.z > 0.0)) cm = (iMouse.xy / iResolution.xy); //mouse circle
    else cm = vec2(0.5*sin(iTime) + .5, 0.5+0.5*cos(iTime));

    float t = 0.0;
    while(t < 0.99)//drawing all points on bezier curve
    {
        if(pointInsideCircle(uv, bezier(c2, cm, c1, t), radius))
        {
            color = vec4(1.0, t, 0.,1.0);
        }
        t+= 0.001;
    }

//drawing 3 circles (red blue and green)
    if(pointInsideCircle(uv, c1, radius))
    {
        color = vec4(1.0, 0.0, 0.0, 1.0);
    }

    if(pointInsideCircle(uv, c2, radius))
    {
        color = vec4(0.0, 1.0, 0.0, 1.0);
    }

    if(pointInsideCircle(uv, cm, radius))
    {
        color = vec4(0.0, 0.0, 1.0, 1.0);
    }



    // Output to screen
    fragColor = color;
}
