vec2 square(vec2 c) {
    return vec2(c.x * c.x - c.y * c.y, 2.0*c.x*c.y);
}

void mainImage( out vec4 fs_out_col, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2.0 - 1.0;


    vec3 col = vec3(1, 0, 0);
    //vec3(1);
    // col[1] = 1.0;
    // col.y = 1.0;
    col.rgb = vec3(1);

/*
    if (distance(uv, vec2(0, 0)) <= 1.0) {
        col = vec3(1,0,0);
    }

    if (abs(distance(uv, vec2(0, 0)) - 1.0) <= 0.005) {
        col = vec3(1,0,0);
    }
    */



/*
    Feladat: az idő múlásával nagyítsuk a képet a (0.15294, 1.0397) pont körül
*/
    // iTime: eltelt idő (reset time gombra nyomva nullázható)

    float zoom = 5.0/exp(iTime);

    vec2 c = uv*zoom - vec2(1.401155, 0);
    vec2 z = vec2(0);

    col = vec3(0, 1.0-iTime/17.5, 0);

    for (int i = 0; i < 1500; ++i) {
        z = square(z) + c;
        if (length(z) > 2.0) {
            col = vec3(0, 0, 0);
            break;
        }
    }


    // col.gb == vec2(col.g, col.b)
    // col.rr == vec2(col.r, col.r)

    // Output to screen
    fs_out_col = vec4(col, 1.0);
}
