// https://twitter.com/XorDev/status/1563001681063079936

float snoise2D2(vec2 p) {   
    p *= 10.52;
    return sin(p.x)*.5 + cos(p.y)*.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{     
    float t = iTime;
    vec3 col;
    vec3 d = vec3(gl_FragCoord.xy*2.-iResolution.xy,iResolution)/iResolution.x,p;
    for(
        float i,s;
        i++ < 2e2;
        p += d*(p.y + (sin(t)*.1)+ .3 -.2  * snoise2D2((p.xz*.7+t*.2+sin(t)*.4)*s))/s) s = exp(mod(i,7.));

    col.grb = .5*d+(.03+sin(t)*.02)*++d / length(d.xy-1.3-sin(t)*.2)-.7 / ++p.z - min(.1+p+p,0.).y;

    fragColor = vec4(col,1.0);
}
