#define PI 3.1415

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float freqs[4];
    freqs[0] = texture( iChannel0, vec2( 0.01, 0.25 ) ).x;
	freqs[1] = texture( iChannel0, vec2( 0.07, 0.25 ) ).x;
	freqs[2] = texture( iChannel0, vec2( 0.15, 0.25 ) ).x;
	freqs[3] = texture( iChannel0, vec2( 0.90, 0.25 ) ).x;

    float n = 4.*(pow(freqs[0],1.)-.5) +(2.0+sin(iTime/2.))*2.+ iMouse.x/112.;//(2.0+sin(iTime/2.))*2.;
    float m = iMouse.y/iResolution.y;
    m = 0.;
    n = 4.*(pow(freqs[0],1.)-.5) +(2.0+sin(iTime/2.))*2.;
    // n = 4.1;
    int passes= 0;
    vec2 uv = fragCoord/iResolution.xy;
    uv -=.5;
    float asp = iResolution.x/iResolution.y;
    uv.x*=asp;

    float d, col;


	//radio
    //edgecenter
    uv+=vec2(0.,0.5);
    d = length(uv);
   	col += cos(2.*PI*n*d + m*float(passes));
    passes++;
    uv-=vec2(0.,0.5);

    uv-=vec2(0.,0.5);
    d = length(uv);
   	col += cos(2.*PI*n*d + m*float(passes));
    passes++;
    uv+=vec2(0.,0.5);

    uv+=vec2(0.5,0.);
    d = length(uv);
   	col += cos(2.*PI*n*d + m*float(passes));
    passes++;
    uv-=vec2(0.5,0.);

    uv-=vec2(0.5,0.);
    d = length(uv);
   	col += cos(2.*PI*n*d + m*float(passes));
    passes++;
    uv+=vec2(0.5,0.);

    //corner
    uv+=0.5;
    d = length(uv);
   	col += cos(2.*PI*n*d + m*float(passes));
    passes++;
    uv-=0.5;

    uv-=0.5;
    d = length(uv);
   	col += cos(2.*PI*n*d + m*float(passes));
    passes++;
    uv+=0.5;

    uv+=vec2(0.5,-.5);
    d = length(uv);
   	col += cos(2.*PI*n*d + m*float(passes));
    passes++;
    uv-=vec2(0.5,-.5);

    uv-=vec2(0.5,-.5);
    d = length(uv);
   	col += cos(2.*PI*n*d + m*float(passes));
    passes++;
    uv+=vec2(0.5,-.5);

    col += 5.*freqs[3];
    col *= .3 + 3.*freqs[3];
    col /= float(passes);

    col = step(uv.x,.5)*step(-.5,uv.x)*col;

    float col1 = smoothstep(-0.5,0.5,col);

    col1 = col1*(1.-col1)*4.;

    vec3 a = vec3(0.5,0.5,0.5);
    vec3 b = vec3(0.5,0.5,0.5);
    vec3 c = vec3(2.4,2.4,2.4);
    vec3 d1 = vec3(0.0,0.33,0.67);

    vec3 col2 = a + b*cos(2.*PI*(c*(max(col , col1)+iTime*0.3) + d1));

    //vec4 c = vec4(1.-abs(col));

    fragColor = vec4(col2,1.);

}
