float Circle(vec2 uv, vec2 p, float r, float blur){
  float d = length(uv-p);
  float c = smoothstep(r, r-blur, d);

   return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    uv-= .5;
   uv.x *= iResolution.x/iResolution.y;

    float c = Circle(uv,vec2(0.0,0.0),0.45,0.1);

    c+= Circle(uv, vec2(-0.15,0.1), 0.2 , 0.2);
    c+= Circle(uv, vec2(0.15,0.1), 0.2 , 0.2);   
    c-= Circle(uv,vec2(-0.15,0.1), 0.1 , 0.1)*2.0;
    c-= Circle(uv, vec2(0.15,0.1), 0.1 , 0.1)*2.0;

    float mouth = Circle(uv, vec2(0.0,0.020), 0.35,0.1);
    mouth -= Circle(uv, vec2(0.0,0.25),0.45,0.1);//topMouth
    mouth =clamp(mouth,0.0,1.0);

   c-= mouth;

    fragColor = vec4(vec3(c*col),1.0);
}
