float Circle(vec2 uv, vec2 position, float r, float blur) 
    {
        float d = length(uv - position);
        float c = smoothstep(r, r-blur, d);
        return c;
    }

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 uv = fragCoord.xy / iResolution.xy; 
    uv -= 0.5;  
    uv.x *= (iResolution.x / iResolution.y); 
    // голова
    float c = Circle(uv, vec2(0.0, 0.2), 0.2, 0.003);
    // глаза
    c -= Circle(uv, vec2(0.1, 0.25), 0.05, 0.01);    
    c -= Circle(uv, vec2(-0.1, 0.25), 0.05, 0.01);              
    // уши
    c += Circle(uv, vec2(0.3, 0.3), 0.15, 0.005);
    c += Circle(uv, vec2(-0.3, 0.3), 0.15, 0.005);
    // тело
    c += Circle(uv, vec2(0.0, -0.15), 0.25, 0.005);
    //руки
    c += Circle(uv, vec2(0.27, 0.005), 0.09, 0.005);
    c += Circle(uv, vec2(-0.27, -0.005), 0.09, 0.005);
    //ноги
    c += Circle(uv, vec2(0.18, -0.4), 0.1, 0.005);
    c += Circle(uv, vec2(-0.18, -0.4), 0.1, 0.005);    

    fragColor = vec4(vec3(c), 1.0);
}
