    float Oval(vec2 uv, vec2 position, float r, float blur) 
        {
            uv.y *= (iResolution.x / iResolution.y);
            uv.x *= (iResolution.x / iResolution.y);
            float d = length(uv - position);
             float c = smoothstep(r, r-blur, d);
              return c;
        }

    float Circle(vec2 uv, vec2 position, float r, float blur) 
        {
            uv.x *= (iResolution.x / iResolution.y);
            float d = length(uv - position);
             float c = smoothstep(r, r-blur, d);
              return c;
        }

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{   
    // координаты от 0 до 1
    vec2 uv = fragCoord.xy / iResolution.xy;    
          // центр круга в центре экрана;
          uv -= .5; 

     float c = Circle(uv, vec2(.0, -.05), .25, .005);
            //Брови
            c -= Oval(uv, vec2(-.085, .167), .04, .015);    
            c -= Oval(uv, vec2(.085, .167), .04, .015);
            //Уши 
            c += Circle(uv, vec2(-.14, .16), .04, .0001);    
            c += Circle(uv, vec2(.14, .16), .04, .0001);
            // Глаза
            c -= Circle(uv, vec2(-.051, .024), .06, .01);    
            c -= Circle(uv, vec2(.051, .024), .06, .01);
            c -= Circle(uv, vec2(.08, 0.020), .055, .01);    
            c -= Circle(uv, vec2(-.08, 0.020), .055, .01);
            // Зрачки
            c += Circle(uv, vec2(-.038, .021), .02, .0005);    
            c += Circle(uv, vec2(.038, .021), .02, .0005);
            c += Circle(uv, vec2(-.038, .021), .02, .0005);    
            c += Circle(uv, vec2(.038, .021), .02, .0005);
            c += Circle(uv, vec2(-.038, .008), .02, .0005);    
            c += Circle(uv, vec2(.038, .008), .02, .0005);
            c += Circle(uv, vec2(-.038, .008), .02, .0005);    
            c += Circle(uv, vec2(.038, .008), .02, .0005);
            // Шеки
            c += Circle(uv, vec2(.11, -0.08), .06, .01);    
            c += Circle(uv, vec2(-.11, -0.08), .06, .01);
            c += Circle(uv, vec2(.11, -0.08), .06, .001);    
            c += Circle(uv, vec2(-.11, -0.08), .06, .001);
            // Руки
            c += Circle(uv, vec2(.25, -.11), .035, .005);
            c += Circle(uv, vec2(-.25, -.11), .035, .005);
            c += Circle(uv, vec2(.26, -.16), .045, .005);
            c += Circle(uv, vec2(-.26, -.16), .045, .005);
            c += Circle(uv, vec2(.26, -.21), .055, .005);
            c += Circle(uv, vec2(-.26, -.21), .055, .005);
            // Ноги
            c += Circle(uv, vec2(.05, -.31), .045, .005);
            c += Circle(uv, vec2(-.05, -.31), .045, .005);
            c += Oval(uv, vec2(.078, -.62), .078, .005);
            c += Oval(uv, vec2(-.078, -.62), .078, .005);
            // Нос
            c -= Oval(uv, vec2(0.0, -.08), .08, .01);
            c += Oval(uv, vec2(0.0, -.08), .065, .0001);
            c += Oval(uv, vec2(0.0, -.08), .065, .0001);
            // Рот
            c -= Oval(uv, vec2(0.0, -.2), .08, .01);
    // Вывод на экран
    fragColor = vec4(vec3(c), 1.0);
}
