// sine wave 20220324
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    uv *= 10.;
    float f = 0.;
    f += abs(sin((uv.x+iTime*1.5) + exp(uv.y)*abs(sin(uv.x-iTime)*0.1+1.))) * step(uv.y, 4.);// wave
    f += smoothstep(2.2, 2., length(vec2(uv.x-15., uv.y-5.))) * step(4., uv.y);// sun

    fragColor.r = f;
}
