void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mouse = iMouse.xy/iResolution.xy;
    uv -= .5;
    uv *= uv  * uv.y * 2. + sin(iTime);

    vec3 col = vec3(uv.x, 0.0, uv.y);
    col.r = sin(uv.x* 3.14*20.+iTime) + sin(uv.y* 3.14*20.+iTime);
    col.b = sin(uv.x* 3.14*19.+iTime) + sin(uv.y* 3.14*21.+iTime);
    col.g = sin(uv.x* 3.14*10.+iTime*.9) + sin(uv.y* 3.14*20.+iTime);


    //col.rb = step(mouse.xy, col.rb);



    fragColor = vec4(col, 1.0);
}
