#define PI 3.14159

float sineWave( vec2 uv, float k ){
    float p = 1. * (sin(k-iTime)+0.5)/PI;
    float r = clamp(0.99*p, 0.02, 0.99);
    float f = smoothstep(0.1, 0.01, length(fract(uv)-0.5)- r);
    return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    uv *= 25.;

    float f = 0.;
    f += step(uv.x, 0.)*step(uv.y, 0.) * sineWave(uv, floor(uv.x));
    f += step(0., uv.x)*step(uv.y, 0.) * sineWave(uv, floor(uv.x)-floor(uv.y));
    f += step(0., uv.x)*step(0., uv.y) * sineWave(uv, length(uv));
    f += step(uv.x, 0.)*step(0., uv.y) * sineWave(uv, sqrt(length(uv)));

    fragColor.r = f;
}
