// variant of https://shadertoy.com/view/NtXyzM
// variant of https://shadertoy.com/view/NlfcRM

//#define S
#define S(v) ( v - 6.283*sign(v)*step(3.14,abs(v)) )       // fight wrap discontinuity
#define fwidth2(v) length( S( vec2( dFdx(v), dFdy(v) ) ) ) // high quality fwidyj
#define fwidth(v)  vec2( fwidth2(v.x), fwidth2(v.y) )        // to manage wrap
#define r          mat2(cos(.3*iTime+vec4(0,-1,1,0)*1.5708)) // rotation
#define LOD(v)     exp2( round(log2(abs(.5/(v)))))

void mainImage( out vec4 O, vec2 u )
{
    vec3 R = iResolution,
         P = vec3( 2.*u - R.xy, 0 ) / R.y;                 // P on sphere surface (or background)
    float l = length(P), n = 1.;                           // n : more lines 
    P.z = sqrt(1.-l*l);
    P.xy *= r; P.yz *= r;                                  // rotations
    //if (P.z<0.) P.x = -P.x;   
    vec2 U = vec2(  sign(P.z)*acos(clamp(P.x/sqrt(1.-P.y*P.y),-1.,1.) ), // polar coords
                   asin(P.y) ),
         V,
         d = vec2( fwidth2(U.x) , fwidth2(U.y) ),
         z = LOD(d) ,                                      // LOD 
         f = .5 + log2(abs(.5/d)) - log2(z),               // fractional part
         p = fwidth(U) * z*n;
         U *= z*n;
    V = abs(sin(U)/(cos(U)*p));                            // lines
    f = max(1.5*f-.5 ,0.);                                 // shorter transition
    int i = 0*int(2.*u.x/R.x);                             // screen side chooser
    O = l > 1. 
          ? vec4(.5)                                       // background
          : vec4( abs(sin(U/2.))[i] < .5 
                    ? V[i]                                 // plain line
                    : mix( 1., V[i], f[i] ));              // transition lines
       // : vec4( abs(cos(U)/(sin(U)*p))[i] , f[i],0,0);    

    O = sqrt(O);                                           // to sRGB
}
