void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv-=.5f;

    //grt time(much easier than in vs2019)
    float t=iTime;

    float d=length(uv);
    float an=d+sin(t+.5f);

    vec2 ret=fract(5.*an*uv);

    // Output to screen
    fragColor = vec4(ret,1.,1.0);
}
