#define BARS 8.0
#define SPEED 4.0

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	// Normalized pixel coordinates (from 0 to 1)
	vec2 xy = fragCoord/(iResolution.xy/ BARS );

	// Time varying pixel color
	vec3 pixel = vec3(0.0);

	float lineDistance = 1.0;
	float lineThickness = lineDistance / 2.0;
	if (mod(xy.x + iTime*SPEED, lineDistance) < lineThickness)
		pixel = vec3(1.0);

	// Output to screen
	fragColor = vec4(pixel, 1.0);
}
