void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 mouse = fragCoord/(iMouse.xy);
    vec2 uv = fragCoord/iResolution.xx;
    float x = iMouse.x / iResolution.x;
    float y = iMouse.y / iResolution.x;
    float ruv = length(uv - vec2(x,y));
    vec3 wave = vec3(step(fract(ruv*10.0 + iTime),0.9),1.0,1.0);
    fragColor = vec4(wave,1.0);
}
