#define SAMPLES 10
#define SHARPNESS 4.0

#define SAMPLES_F float(SAMPLES)

#define TAU  6.28318530718

// Hash without Sine - https://www.shadertoy.com/view/4djSRW
#define HASHSCALE1 443.8975
float hash13(vec3 p3)
{
	p3  = fract(p3 * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float r = 0.05 + 0.05 * sin(iTime*.2*TAU+4.3);
    if (iMouse.w >= 1.0)
    {
    	r = (iMouse.x / iResolution.x) * 0.1;
    }

    float d = (r * 1.0 * iResolution.x) / SAMPLES_F;
    float lod = log2(max(d / SHARPNESS, 1.0));

    vec4 color = vec4(0.0, 0.0, 0.0, 0.0);    
	for (int i = 0; i < SAMPLES; ++i)
    {
        float fi = float(i);
        float rnd = hash13(vec3(uv.xy, fi));
        float f = (fi + rnd) / SAMPLES_F;	// stratified sampling between [0, 1]
        f = (f * 2.0 - 1.0) * r;	// [0, 1] -> [-r, r]

    	color += textureLod(iChannel0, uv + vec2(f, 0.0), lod);
    }
    color = color / SAMPLES_F;

    fragColor = color;
}
