# define PI 3.1415

# define circlesX 35
# define circlesY 20
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // UVs
    vec2 uv = fragCoord / iResolution.xy;
    // Pixel coordinates (X: 0 - 35, Y: 0 - 20)
    vec2 coords = uv * vec2(circlesX, circlesY);

    // Noise
    vec4 noiseCol = texture(iChannel0, floor(coords) / vec2(circlesX, circlesY));

    // Times
    float offsetTime = noiseCol.r;
    float absSinTime = abs(sin(iTime + offsetTime * PI));

    vec2 circlePos = fract(coords);
    float circles = step(0.5 / absSinTime, 1.0 - distance(circlePos, vec2(0.5)));

    fragColor = vec4(vec3(circles * absSinTime, 0.0, circles * absSinTime), 1.0);
}
