const vec3 blue = vec3(0.0,0.0,1.0);
const vec3 black = vec3(0.0,0.0,0.0);
const vec3 dgreen = vec3(0.0,0.5,0.0);
const vec3 lgreen = vec3(0.0,1.0,0.0);
const vec3 dgray = vec3(0.2,0.2,0.2);
const vec3 lgray = vec3(0.8,0.8,0.8);
const vec3 red = vec3(0.7,0.2,0.2);

void set(inout vec3 c,const in vec3 col,const in vec2 gpos,const in vec2 lpos,const in vec2 size,const in float fade){
    float t=smoothstep(0.0,1.0,(sin(iTime/2.0)*2.0+0.5)/2.0*2.5);
    //float t=0.0;
    vec2 pos = abs((gpos-lpos)/size);
    float circle = length(pos);
    float square = max(pos.x,pos.y);
    float scfade = mix(circle,square, t);
    float colfade = scfade<1.0 ? mix(smoothstep(0.3,1.0,1.0-scfade+0.5),1.0,fade) : 0.0;
    c = mix(c,col,colfade);
}

void xmirror(inout vec3 c,const in vec3 col,const in vec2 gpos,const in vec2 lpos,const in vec2 size,const in float fade){
    set(c,col,gpos,lpos,size,fade);
    set(c,col,gpos*vec2(-1.0,1.0),lpos,size,fade);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 gpos = fragCoord * 2.0 - iResolution.xy;
    gpos/=min(iResolution.x,iResolution.y);

    //background
    vec3 c = blue;
    //face
    set(c,dgreen,gpos,vec2(0.0,-0.2),vec2(1.05),1.0);
    //ear
    xmirror(c,dgreen,gpos,vec2(0.8,0.8),vec2(0.5),1.0);
    xmirror(c,lgreen,gpos,vec2(0.85,0.75),vec2(0.3),0.7);
    //eye
    float blink=smoothstep(0.0,1.0,(cos(iTime/0.7)*2.0+1.9999)/2.0*35.0);
    xmirror(c,black,gpos,vec2(0.58,0.06),vec2(0.14)*vec2(1.1,blink+0.1),1.0);
    //mouth
    set(c,black,gpos,vec2(0.0,-0.9),vec2(0.5,0.3),1.0);
    xmirror(c,lgray,gpos,vec2(0.4,-0.6),vec2(0.2),0.8);
    set(c,red,gpos,vec2(0.0,-0.9),vec2(0.3,0.2),1.0);
    //nose
    set(c,black,gpos,vec2(0.0,-0.4),vec2(0.3),1.0);
    set(c,dgray,gpos,vec2(-0.15,-0.25),vec2(0.08),1.0);

    fragColor = vec4(c,1.0);
}
